mapper.select:
select user_id, sex, age, degree, comment_id, news_content, collect_content
from t_features
where user_id = #{userId,jdbcType=INTEGER}

select user_id, sex, age, degree, comment_id, news_content, collect_content
from t_features
where sex = #{sex,jdbcType=VARCHAR}
and degree = #{degree, jdbcType=VARCHAR}
and age >= #{min, jdbcType=VARCHAR}
and age <= #{max, jdbcType=VARCHAR}
