mapper.select:
select * from t_user where 1=1

  and username like concat('%',#{name},'%')


  and sex=#{sex}

limit #{page},#{limit}

select * from t_admin where 1=1

  and username like concat('%',#{name},'%')


  and sex=#{sex}

limit #{page},#{limit}

select * from t_news where 1=1

  and content like concat('%',#{name},'%')


  and sex=#{sex}

limit #{page},#{limit}

select * from t_collect where 1=1

  and user_id like concat('%',#{name},'%')


  and sex=#{sex}

limit #{page},#{limit}

select * from t_annouce where 1=1

  and content like concat('%',#{name},'%')


  and sex=#{sex}

limit #{page},#{limit}

select * from t_comment where 1=1

  and content like concat('%',#{name},'%')


  and sex=#{sex}

limit #{page},#{limit}

select id
from t_user
where username = #{username,jdbcType=VARCHAR}
