function setTOamt(name, poLeft, yans, timeB) {
	setTimeout(function() {
		name.animate({
			left: poLeft,
			marginLeft: 0,
			opacity: 1
		}, {
			easing: "swing",
			duration: yans
		})
	}, timeB)
}
function setTOamtA(name, yans, timeA) {
	setTimeout(function() {
		name.animate({
			marginTop: 0,
			opacity: 1
		}, {
			easing: "swing",
			duration: yans
		})
	}, timeA)
}
